import React, {useEffect} from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import VendorListTable from './vendor_table';
import axios from '../../utils1.js/axios';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));

export default function FormPropsTextFields() {
  const classes = useStyles();
  const [search, setSearch] = React.useState('');
  const [rows, setRows] = React.useState([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(2);
  const [count, setCount] = React.useState(0);

  useEffect(() => {
		axios.get(`/vendor/vendorlist/?p=${page+1}&records=${rowsPerPage}`).then((res) => {

			console.log(res.data);
            console.log(res.data.results);
            //console.log(res.data.count);
            setCount(res.data.count);
            setRows(res.data.results)
            //setPage(res.data.next);
            //setPagenew(res.data);
		});
  }, [page, rowsPerPage]);

  const handleChange = (event) => {
    console.log(event.target.value);
    setSearch(event.target.value);
    axios.get(`/vendor/vendorlist/?search=${search}`).then((res) => {

			console.log(res.data);
            
            //console.log(res.data.count);
            //setCount(res.data.count);
            //setRows(res.data.results)
            //setPage(res.data.next);
            //setPagenew(res.data);
		});
  };

  useEffect(() => {
		axios.get(`/vendor/vendorlist/?search=${search}`).then((res) => {

			console.log(res.data);
            
            //console.log(res.data.count);
            //setCount(res.data.count);
            //setRows(res.data.results)
            //setPage(res.data.next);
            //setPagenew(res.data);
		});
  }, [search]);

  return (
    <form className={classes.root} noValidate autoComplete="off">
      <div>
        
       
        <TextField id="standard-search" label="Search field" type="search" onChange={handleChange} />
        <VendorListTable rows={rows} count={count} />
      </div>
    </form>
  );
}
