import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import { useDispatch, useSelector } from 'react-redux'
import Modal from '@material-ui/core/Modal';
import BUdelForm from './bu.del.form'
import Fade from '@material-ui/core/Fade';
import TablePagination from '@material-ui/core/TablePagination';



const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        padding: theme.spacing(2, 4, 3),
    },
    table1: {
        minWidth: '35em',
        maxHeight: 300,
        overflowY: 'auto',
    },
    tablecell: {
        fontSize: "1.1em",
    },
    delIcon: {
        fontSize: '2em',
        '&:hover': {
            cursor: 'pointer',
            color: '#D90429',
        }
    }
}));


function rand() {
    return Math.round(Math.random() * 10) - 10;
}

function getModalStyle() {
    const top = 40 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}


export default function BasicTable({data, delStart, label}) {
    const dispatch = useDispatch()
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState({});
    //console.log(data);

    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
  
    const handleChangePage = (event, newPage) => {
      setPage(newPage);
    };
  



    const classes = useStyles();



    const handleOpen = (id) => {
        setOpen(
            {
                [`setOpen-${id}`]: true
            }
        );
    };

    const handleClose = () => {
        setOpen(false);
    };
    console.log(open)
    const body = (row) => (
        <div style={modalStyle} className={classes.paper}>
            <BUdelForm setOpen={setOpen} row={row} delStart={delStart} label={label} />
        </div>
    );

    const handleBackdropClick = () => {
        setOpen(false);
    };

    useEffect(() => {
		axios.get(`/vendor/vendorlist/?p=${page+1}&records=${rowsPerPage}`).then((res) => {

			console.log(res.data);
            console.log(res.data.results);
            //console.log(res.data.count);
            setCount(res.data.count);
            setRows(res.data.results)
            //setPage(res.data.next);
            //setPagenew(res.data);
		});
  }, [page, rowsPerPage]);


    return (
        <React.Fragment>
        <TableContainer className={classes.table1} component={Paper} style={{ marginTop: "1.5em" }}>
            <Table aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell className={classes.tablecell}>Available {label}</TableCell>
                        <TableCell></TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {data.results.map((row) => (
                        <TableRow key={row.id}>
                            <TableCell component="th" scope="row" className={classes.tablecell}>
                                {row.name}
                            </TableCell>
                            <TableCell>

                                <DeleteForeverIcon onClick={() => handleOpen(row.id)} fontSize="default" className={classes.delIcon} />


                                <Modal
                                    aria-labelledby="transition-modal-title"
                                    aria-describedby="transition-modal-description"
                                    className={classes.modal}
                                    open={open[`setOpen-${row.id}`]}
                                    onClose={handleClose}
                                    closeAfterTransition
                                    onBackdropClick={handleBackdropClick}
                                    BackdropProps={{
                                        timeout: 500,
                                    }}

                                >
                                    <Fade in={open}>
                                        {body(row)}
                                    </Fade>
                                </Modal>
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
        <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={count}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
      </React.Fragment>
    );
}
