import React, {useEffect} from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import VendorListTable from './vendor_table';
import axios from '../../utils1.js/axios';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

export default function FormPropsTextFields() {
  const classes = useStyles();
  const [search, setSearch] = React.useState('');
  const [rows, setRows] = React.useState([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(2);
  const [count, setCount] = React.useState(0);
  const [age, setAge] = React.useState('');

  const handleSelectChange = (event) => {
    setAge(event.target.value);
  };

  useEffect(() => {
		axios.get(`/vendor/vendorlist/?p=${page+1}&records=${rowsPerPage}`).then((res) => {

			console.log(res.data);
            console.log(res.data.results);
            //console.log(res.data.count);
            setCount(res.data.count);
            setRows(res.data.results)
            //setPage(res.data.next);
            //setPagenew(res.data);
		});
  }, [page, rowsPerPage]);

  const handleChange = (event) => {
    console.log(event.target.value);
    setSearch(event.target.value);

    axios.get(`/vendor/vendorlist/?search=${search}`).then((res) => {

      console.log(res.data);
      if(res.data){
        setRows(res.data.results);
      }
      

		});
    
  };

  return (
    <form className={classes.root} noValidate autoComplete="off">
      <Grid container justify='space-between'>
        <Grid item>
        <FormControl className={classes.formControl}>
        <InputLabel id="demo-simple-select-label">Select any field to filter</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={age}
          onChange={handleSelectChange}
        >
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
        </Grid>
        <Grid item>
        <TextField id="standard-search" label="Search field" type="search" onChange={handleChange} />
        </Grid>
      </Grid>

      
        
       
        
        <VendorListTable rows={rows} count={count} rowsPerPage={rowsPerPage} setRowsPerPage={setRowsPerPage} page={page} setPage={setPage}/>
      
    </form>
  );
}
